SELECT id,title,content,MATCH(title,content) AGAINST('database performance' IN NATURAL LANGUAGE MODE) AS relevance_score,MATCH(title,content) AGAINST('database performance' IN BOOLEAN MODE) AS boolean_score,MATCH(title,content) AGAINST('database performance' WITH QUERY EXPANSION) AS expansion_score FROM articles WHERE MATCH(title,content) AGAINST('database performance' IN NATURAL LANGUAGE MODE) ORDER BY relevance_score DESC;